# Trigger "ins_psb_biaya"
#

DROP TRIGGER IF EXISTS `ins_psb_biaya`;
CREATE DEFINER='root'@'localhost' TRIGGER `cobatrigger2`.`ins_psb_biaya` AFTER INSERT ON `cobatrigger2`.`psb_biaya`
  FOR EACH ROW BEGIN

	/*loop golongan */
		BLOCK1: begin
			declare vGolongan int;
			declare no_more_rows1 INT DEFAULT 0;  
			declare cursor1 cursor for
					select replid
					from  psb_golongan;
			declare continue handler for not found set no_more_rows1 =1;
			open cursor1;
			LOOP1: loop
					fetch cursor1
					into vGolongan;
					if no_more_rows1 then
							close cursor1;
							leave LOOP1;
					end if;
					
					/*loop detail-gelombang*/
					BLOCK2: begin
						declare vDetGelombang int;
						/*declare vDepartemen int;*/
						declare no_more_rows2 INT DEFAULT 0;  
						declare cursor2 cursor for
								select replid/* ,departemen*/
								from  psb_detailgelombang;
						declare continue handler for not found set no_more_rows2=1;
						open cursor2;
						LOOP2: loop
								fetch cursor2
								into  vDetGelombang;
								if no_more_rows2 then
										close cursor2;
										leave LOOP2;
								end if;

								/*loop subtingkat */
								BLOCK3: begin
									declare vSubtingkat int;
									declare no_more_rows3 INT DEFAULT 0;  
									declare cursor3 cursor for
											select st.replid 
											from aka_subtingkat st 
												JOIN aka_kelas k on k.subtingkat = st.replid
												/*WHERE k.departemen = vDepartemen*/
											GROUP BY st.replid;
									declare continue handler for not found set no_more_rows3=1;
									open cursor3;
									LOOP3: loop
											fetch cursor3
											into  vSubtingkat;
											if no_more_rows3 then
													close cursor3;
													leave LOOP3;
											end if;

											INSERT INTO psb_detailbiaya SET 
												biaya = NEW.replid, 
												detailgelombang = vDetGelombang, 
												subtingkat = vSubtingkat,
												golongan  = vGolongan;
										end loop LOOP3;
								end BLOCK3;
						end loop LOOP2;
					end BLOCK2;
			end loop LOOP1;
	end BLOCK1;
	END;
